sql.foreach:
(
 SELECT tbl2.address_no
	  FROM (
		  SELECT (@ROWNUM := @ROWNUM + 1) rn, tbl1.*
			  FROM (
					SELECT *
					FROM address
					WHERE address_user_no = #{userNo}
					ORDER BY address_representative DESC, address_no 
				) tbl1,
				( SELECT @ROWNUM := 0 ) R
		) tbl2
  WHERE rn = #{item}
  )

mapper.select:
SELECT COUNT(*) FROM address
WHERE address_user_no = #{userNo}
	AND REPLACE(address_detail,' ','') = #{OrderAddressDetail}
	AND address_zip_num = #{zipNum}

SELECT * FROM
	(
	    SELECT (@ROWNUM := @ROWNUM + 1) rn, tbl.*
	    FROM
		    (
			    SELECT * FROM address
			    WHERE address_user_no = #{userNo}
			    ORDER BY address_representative DESC, address_no 
		    ) tbl,
		    (SELECT @ROWNUM := 0) R
	)
WHERE rn = #{addressRn}
