mapper.select:
SELECT *
FROM
    (
    SELECT ROWNUM AS rn, tbl.* FROM
        (
        SELECT * FROM product
        WHERE product_category_no = #{categoryNo}
        ORDER BY product_reg_date DESC
        ) tbl
    )

mapper.update:
UPDATE board
SET board_title=#{boardTitle}, board_content=#{boardContent}, board_reg_date = sysdate
WHERE board_no = #{boardNo}

mapper.select:

SELECT *
FROM
    (
    SELECT ROWNUM AS rn, tbl.* FROM
        (
            SELECT *
            FROM notice
            ORDER BY notice_no DESC
        ) tbl
    ) s
WHERE rn > (#{pageNum}-1) * #{cpp}
AND rn <= #{pageNum} * #{cpp}

SELECT s.board_no FROM
	(
	SELECT ROWNUM AS rn, tbl.* FROM 
		(
			SELECT board_no FROM board
			WHERE board_user_no = #{boardUserNo}
			ORDER BY board_reg_date DESC
		) tbl
	) s
WHERE rn = 1


SELECT *
FROM
    (
    SELECT ROWNUM AS rn, tbl.* FROM
        (
            SELECT *
            FROM board
            WHERE board_user_no=#{userNo}
        ) tbl
    ) s
WHERE rn > (#{page.pageNum}-1) * #{page.cpp}
AND rn <= #{page.pageNum} * #{page.cpp}
