mapper.select:

SELECT *
FROM
    (
		SELECT (@ROWNUM := @ROWNUM + 1) rn, tbl.* 
		FROM
			(
				SELECT d.*, c.*, u.user_nickname AS writer
				FROM board d LEFT JOIN category c ON d.board_category_no = c.category_no
					     	 LEFT JOIN duck_user u ON d.board_user_no = u.user_no
				WHERE d.board_category_no = #{categoryNo}
			) tbl,
			(SELECT @ROWNUM := 0) R
		ORDER BY tbl.board_reg_date DESC
    ) tbl2
WHERE rn > (#{paging.pageNum}-1) * #{paging.cpp}
AND rn <=(#{paging.pageNum}) * #{paging.cpp}

mapper.update:
UPDATE board
SET board_title = #{boardTitle}, board_content = #{boardContent}, 

WHERE board_no = #{boardNo}

mapper.select:

SELECT *
FROM
    (
	    SELECT (@ROWNUM := @ROWNUM + 1) rn, tbl.* FROM
			(
			    SELECT *
			    FROM notice
			) tbl,
			(SELECT @ROWNUM := 0) R
		ORDER BY tbl.notice_no DESC
    ) tbl2
WHERE rn > (#{pageNum}-1) * #{cpp}
AND rn <= (#{pageNum}) * #{cpp}
