mapper.select:

	SELECT * FROM
 	  	(
	  	SELECT ROWNUM AS rn, tbl.* FROM
			 (
                SELECT * FROM reply r
                WHERE r.reply_board_no = #{bno}
                ORDER BY r.reply_no DESC
              ) tbl
        )
		WHERE rn > (#{paging.pageNum}-1) * #{paging.cpp}
		AND rn <= #{paging.pageNum} * #{paging.cpp}

SELECT COUNT(*)
FROM reply
WHERE reply_board_no = #{bno}
