mapper.insert:
INSERT INTO duck_order (order_product_no, order_num, order_user_no, order_quantity, order_price
		, order_recipient_name, order_recipient_tel, order_address_zip_num, order_address_basic, order_address_detail
		, order_payment_method, order_return_bank, order_return_account, payment_key)
VALUES(#{orderProductNo}, #{orderNum}, #{orderUserNo}, #{orderQuantity}, #{orderPrice}
, #{orderRecipientName}, #{orderRecipientTel}, #{orderAddressZipNum}, #{orderAddressBasic}, #{orderAddressDetail}
, #{orderPaymentMethod}, #{orderReturnBank}, #{orderReturnAccount}, #{paymentKey})

mapper.select:
SELECT category_minor_title
FROM category
WHERE category_major_title = #{major}

SELECT * FROM duck_order
WHERE order_user_no = #{userNo}
ORDER BY order_no DESC

mapper.update:
UPDATE product SET product_name = #{productName}, product_price_normal = #{productPriceNormal}, product_price_selling = #{productPriceSelling}
			, product_detail = #{productDetail}, product_stock = #{productStock}, product_category_no = #{productCategoryNo}
WHERE product_no = #{productNo}

mapper.select:


SELECT * FROM product
WHERE product_category_no = #{categoryNo}
ORDER BY product_reg_date DESC
